import commandLineArgs from "command-line-args";
import { copyFromToRecursive } from "../actions/copy-from-to.js";
import { error } from "../utils/log.js";
//  npm run tool:copy-recursive -- "C:\git\src" "C:\git\dst"
const optionDefinitions = [
    { name: 'paths', type: String, multiple: true, defaultOption: true },
];
const options = commandLineArgs(optionDefinitions);
if (!options.paths || options.paths.length !== 2) {
    error("Please specify two paths");
    process.exit(1);
}
copyFromToRecursive(options.paths[0], options.paths[1]);
