import { generateFontAtlas } from '../actions/generate-font-atlas.js';
import commandLineArgs from "command-line-args";
import { error, warning } from "../utils/log.js";
const optionDefinitions = [
    { name: 'fontPath', type: String, multiple: false },
    { name: 'targetDirectory', type: String, multiple: false },
    { name: 'charset', type: String, multiple: false },
];
const options = commandLineArgs(optionDefinitions);
// console.log(options);
if (!options.fontPath) {
    error("Please specify a font path as --font-path <path>");
    process.exit(1);
}
if (!options.targetDirectory) {
    error("Please specify a target directory as --target-directory <path>");
    process.exit(1);
}
if (!options.charset) {
    warning("No charset specified, using default charset");
}
run();
async function run() {
    const res = await generateFontAtlas(options.fontPath, options.targetDirectory, options.charset);
    if (!res)
        process.exit(1);
}
