import commandLineArgs from "command-line-args";
import { generateUnfurlImage } from "../actions/text-to-image.js";
import { error, warning } from "../utils/log.js";
const optionDefinitions = [
    { name: 'title', type: String, multiple: false },
    { name: 'description', type: String, multiple: false },
    { name: 'backgroundImage', type: String, multiple: false },
    { name: 'targetPath', type: String, multiple: false },
];
const options = commandLineArgs(optionDefinitions, { partial: true });
if (!options.title) {
    error("Please specify a title as --title <title>");
    process.exit(1);
}
if (!options.description) {
    error("Please specify a description as --description <description>");
    process.exit(1);
}
if (!options.targetPath) {
    error("Please specify a target path as --targetPath <path>");
    process.exit(1);
}
if (!options.backgroundImage) {
    warning("Missing background image, you can specify it as --backgroundImage <path>");
}
generateUnfurlImage(options.title, options.description, options.backgroundImage, options.targetPath);
