// npm run tool:install-packages "path/to/package.json" --dependencies "@needle-tools/engine:my/path"
import commandLineArgs from "command-line-args";
import { addPackages } from "../actions/install-package.js";
import { error } from "../utils/log.js";
const optionDefinitions = [
    { name: 'packagejson', type: String, multiple: false, defaultOption: true },
    { name: 'dependencies', type: String, multiple: true },
];
const options = commandLineArgs(optionDefinitions);
if (!options.packagejson || options.packagejson.length === 0) {
    error("Please specify a package.json file");
    process.exit(1);
}
if (!options.dependencies || options.dependencies.length === 0) {
    error("Please specify dependencies");
    process.exit(1);
}
addPackages(options.packagejson, options.dependencies, undefined, undefined);
