import commandLineArgs from "command-line-args";
import { stopProcesses } from "../actions/stop-processes.js";
import { error } from "../utils/log.js";
const optionDefinitions = [
    { name: 'pattern', type: String, multiple: false, defaultOption: true }
];
const options = commandLineArgs(optionDefinitions);
if (!options.pattern || options.pattern.length === 0) {
    error("Please specify a pattern: " + options.pattern);
    process.exit(1);
}
stopProcesses(options.pattern);
