import commandLineArgs from "command-line-args";
import { error } from "../utils/log.js";
import { publishFolderToGithubPages } from "../actions/publish-to-github-pages.js";
const optionDefinitions = [
    { name: 'directory', type: String, multiple: false, defaultOption: true },
    { name: 'repository', type: String, multiple: false }
];
const options = commandLineArgs(optionDefinitions);
if (!options.directory || options.directory.length === 0) {
    error("Please specify \"--directory\" as a path to a folder to publish");
    process.exit(1);
}
publishFolderToGithubPages(options.directory, options.repository);
