import commandLineArgs from "command-line-args";
import { updateTypesRecursiveFromEngine } from "../actions/update-types.js";
import { error } from "../utils/log.js";
// > npm run tool:update-packages "C:\git\path\to\project" -- --packages @needle-tools/engine
const optionDefinitions = [
    { name: 'directory', type: String, multiple: true, defaultOption: true }
];
const options = commandLineArgs(optionDefinitions);
if (process.argv?.includes("debug"))
    console.log(options);
if (!options.directory || options.directory.length === 0) {
    error("Please specify one or more packages to update");
    process.exit(1);
}
for (const directory of options.directory) {
    updateTypesRecursiveFromEngine(directory);
}
