import commandLineArgs from "command-line-args";
import { upload_to_glitch } from "../actions/upload.js";
const optionDefinitions = [
    { name: 'file', type: String, multiple: false, defaultOption: true },
    { name: 'glitch', type: String, multiple: false },
    { name: 'key', type: String, multiple: false }
];
const options = commandLineArgs(optionDefinitions);
if (!options.file || options.file.length === 0) {
    console.error("No file specified. Use --file <file>");
    process.exit(1);
}
if (!options.glitch || options.glitch.length === 0) {
    console.error("No Glitch endpoint specified. Use --glitch <url>, e.g. https://my-glitch-name.glitch.me");
    process.exit(1);
}
if (!options.key || options.key.length === 0) {
    console.error("No deployment key specified. Use --key <key>");
    process.exit(1);
}
upload_to_glitch(options.file, options.glitch, options.key);
