import commandLineArgs from "command-line-args";
import { uploadToNeedleCloud } from "../actions/cloud";
const optionDefinitions = [
    { name: 'dir', type: String, multiple: false, defaultOption: true },
    { name: 'key', type: String, multiple: false }
];
const options = commandLineArgs(optionDefinitions);
if (!options.dir || options.dir.length === 0) {
    console.error("No file specified. Use --dir <dir>");
    process.exit(1);
}
if (!options.key || options.key.length === 0) {
    console.error("No deployment key specified. Use --key <key>");
    process.exit(1);
}
uploadToNeedleCloud({
    dir: options.dir,
    key: options.key
});
