import { existsSync } from "fs";
import path from "path";
export function findGitDirectory(dir) {
    if (!existsSync(dir))
        return false;
    const git = path.join(dir, '.git');
    if (existsSync(git)) {
        return dir;
    }
    const parentDir = path.dirname(dir);
    if (parentDir === dir) {
        return null;
    }
    return findGitDirectory(parentDir);
}
