import { existsSync, statSync } from "fs";
import path from "path";
import os from "os";
export function getCurrentDirectory() {
    if (process.env.INIT_CWD) {
        return process.env.INIT_CWD;
    }
    return process.cwd();
}
export function getPackageJsonPath(str) {
    if (str.endsWith("package.json")) {
        return str;
    }
    const stat = statSync(str);
    if (stat.isFile()) {
        str = path.dirname(str);
    }
    return path.join(str, "package.json");
}
export function getRelative(currentPath, targetPath) {
    if (!path.isAbsolute(targetPath)) {
        console.log("isRelative", targetPath);
        return targetPath;
    }
    // check if its a directory
    const stat = statSync(currentPath);
    if (stat.isFile()) {
        currentPath = path.dirname(currentPath);
    }
    const relativePath = path.relative(currentPath, targetPath);
    return relativePath.replace(/\\/g, '/');
}
export function exists(p) {
    return existsSync(p);
}
export function getOSTempDirectory() {
    return os.tmpdir();
}
