import { exec } from "child_process";
export function openUrlInDefaultBrowser(url) {
    if (process.platform === 'win32') {
        return exec('start ' + url);
    }
    else if (process.platform === 'darwin') {
        return exec('open ' + url);
    }
    else {
        return exec('xdg-open ' + url);
    }
}
